            font-size: 40px;
            font-weight: 500;
            padding-right: 50px;
            padding-left: 15px;
        }

        .scoreInfo {
            color: hsl(0, 0%, 62%);
        }

        .scoreLow {
            color: hsl(187, 100%, 42%)
        }

        .scoreMedium {
            color: hsl(66, 70%, 54%)
        }

        .scoreHigh {
            color: hsl(45, 100%, 51%)
        }

        .scoreCritical {
            color: hsl(4, 90%, 58%)
        }



        .description {
            vertical-align: middle;
            height: fit-content;
            margin-right: 25px;
            text-justify: distribute;
            flex:1;
            word-wrap: break-word;
            width: 0;
