   */
  function transposeArray(array) {
    const n = array.length;
    if (n === 0) {
      return [];
    }
    const m = array[0]?.length ?? 0;
    if (m === 0) {
      return [];
    }

    let transposedArray = new Array(m);
    for (let i = 0; i < m; i++) {
      transposedArray[i] = new Array(n);
    }

    for (let i = 0; i < n; i++) {
      for (let j = 0; j < m; j++) {
        transposedArray[j][i] = array[i][j];
      }
    }

    return transposedArray;
