    .wrapper {
      display: flex;
      height: 100vh;
    }

    /*left sidebar*/
    .left-side {
      min-width: var(--sidebar-width);
      max-width: var(--sidebar-width);
      overflow-y: auto;
      padding-right: 6px;
      border-right-width: var(--divider-border-width);
    }

    .left-side-item {
      width: 100%;
      padding: var(--sidebar-item-padding);
      font: var(--sidebar-item-text-style);
    }

    .left-side-item.active {
      background-color: var(--sidebar-item-active-bg);
      font: var(--sidebar-item-text-style-active);
    }

    /*left sidebar*/

    .right-side {
      width: calc(100% - 168px);
      display: flex;
      flex-direction: column;
    }

    .header {
      display: flex;
      align-items: center;
      justify-content: space-between;
      gap: 12px;
      margin-bottom: 12px;
      border-bottom-width: var(--divider-border-width);
    }

    .header-title {
      font: var(--header-title-font);
      word-break: break-word;
      padding: 0 6px;
    }

    .logo {
      min-width: var(--logo-size);
      min-height: var(--logo-size);
      max-width: var(--logo-size);
      max-height: var(--logo-size);
      color: var(--logo-color);
    }

    .main-content {
      flex: 1;
      overflow: auto;
    }

    .list:has(.list-item) {
      margin-bottom: 32px;
    }

    .list-item {
      display: flex;
      align-items: flex-start;
      gap: 6px;
      padding: 3px 6px;
    }

    .list-item:hover {
      background-color: var(--widget-row-highlight-bg);
    }

    .list-item + .list-item {
      margin-top: 6px;
    }

    .list-item-text {
      width: 50%;
      max-width: 300px;
      word-break: break-word;
    }

    .list-item-text-key {
      font: var(--list-item-key-font);
      text-transform: uppercase;
    }

    .list-item-text-value {
      font: var(--list-item-value-font);
    }

    .list-item-status-value {
      color: rgb(200, 200, 200);
    }

    .list-item-specific-value {
      display: flex;
      align-items: center;
      gap: 8px;
    }

    .list-item-success-value {
      color: rgb(153, 215, 201);
    }

    .list-item-warning-value {
      color: rgb(255, 243, 113);
    }

    .list-item-error-value {
      color: rgba(255, 90, 80);
    }

    .list-item-status-value:first-letter {
      text-transform: uppercase;
    }

    .list-item-screenshot {
      justify-content: center;
    }

    .list-item-screenshot-text {
      display: none;
    }

    @media screen and (min-width: 700px) {
      .list-item-text-value {
        max-width: 100%;
        width: 100%;
      }

      .list-item-screenshot-text {
        display: block;
      }
    }

    .tag {
      display: flex;
      flex-wrap: wrap;
      gap: 8px;
    }

    .tag-item {
      padding: 2px 12px;
      background-color: rgb(106, 113, 128);
      border-radius: 2px;
    }

    /*Table*/
    .table-wrapper {
      padding: 0 6px;
    }

    .table-wrapper + .table-wrapper {
      margin-top: 8px;
    }

    .table-header {
      display: flex;
      align-items: center;
      justify-content: space-between;
      gap: 12px;
      margin-bottom: 12px;
    }

    .table-title {
      font: var(--table-title-size);
      text-transform: uppercase;
    }

    .table-row-count {
      display: flex;
      margin-right: auto;
      align-items: center;
      white-space: nowrap;
      gap: 6px;
      font: var(--table-title-size);
    }

    .table-search-input {
      max-width: 200px;
    }

    @media screen and (max-width: 700px) {
      .table-search-input {
        max-width: 150px;
      }
    }

    .table-container {
      overflow: auto;
    }

    .table {
      table-layout: fixed;
    }

    .table thead {
      position: sticky;
      top: 0;
      z-index: 9;
    }

    .table th {
      width: 250px;
      background-color: var(--widget-row-highlight-bg);
      color: var(--widget-text-color);
      font: var(--table-th-size);
      padding: 6px 12px;
      text-transform: uppercase;
      vertical-align: top;
      text-align: left;
    }

    .table tr:nth-child(even) {
      background-color: var(--table-color-even-row);
    }

    .table-resizable-line {
      position: absolute;
      inset: 3px 6px 3px auto;
      width: 2px;
      cursor: col-resize;
      user-select: none;
      background-color: rgb(98, 117, 163);
      z-index: 9;
    }

    .table-resizable-line::before, .table-resizable-line::after {
      content: '';
      position: absolute;
      inset: 0 auto;
      width: 6px;
    }

    .table-resizable-line::before {
      left: -6px;
    }

    .table-resizable-line::after {
      right: -6px;
    }

    .table td {
      padding: 8px 12px;
      font: var(--table-td-size);
      color: var(--widget-text-color);
      vertical-align: top;
    }

    .table-button {
      display: flex;
      align-items: center;
      gap: 6px;
    }

    .table-button-text {
      text-decoration: underline;
      line-height: 20px;
    }

    .table-button-icon {
      width: 20px;
      height: 20px;
    }

    .modal {
      position: fixed;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      background-color: var(--widget-bg);
      box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
      padding: 16px;
      max-width: 90%;
      min-width: 50%;
      max-height: 90%;
      border-radius: 8px;
      z-index: 9999;
      overflow: hidden;
      display: flex;
      flex-direction: column;
    }

    .modal-content {
      overflow: auto;
    }

    .modal-content img {
      display: flex;
      max-width: 100%;
      max-height: 100%;
      margin: 0 auto;
    }

    .modal-content pre {
      font: var(--table-td-size);
    }

    .modal-header {
      margin-bottom: 8px;
      display: flex;
      align-items: center;
      justify-content: space-between;
      gap: 6px;
    }

    .modal-title {
      font: 600 20px/26px var(--widget-font-family);
      word-wrap: break-word;
      flex: 1;
    }

    .modal-close {
      width: 24px;
      height: 24px;
      display: flex;
      align-items: center;
      justify-content: flex-end;
    }

    .modal-close svg {
      width: 20px;
      height: 20px;
      stroke: #fff;
    }

    .modal-overlay {
      position: fixed;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      background-color: rgba(0, 0, 0, 0.5);
      z-index: 999;
    }
  </style>
 </head>
 <body>
