<script>
  // JSON placeholder [{stepInstanceName}.JsonResult]
  const actionListData = [{stepInstanceName}.JsonResult];

  // Constant for fields that are allowed to be modified
  const allowlistedFields = ["attributed_associations", "mscore", "date_of_disclosure", "exploitation_state", "mve_id", "risk_rating", "report_link", "type", "observed", "description", "aliases", "motivations", "industries", "malware", "tools", "locations", "cve", "associated_uncs", "executive_summary", "analysis", "exploitation_consequence", "exploitation_vectors", "vendor_fix_references", "sources", "capabilities", "operating_systems", "roles"];

  // Constants for tooltip positioning
  const tooltipGap = 25;
  const tooltipTriangleHeight = 8;
  const tooltipTriangleMinGap = 5;
  const triangleWidth = 16;
  let tooltipTimeout = undefined;

  const tooltipDetectResizeObserver = new ResizeObserver((entries) => {
    for (const entry of entries) {
      const target = entry.target;
      if (target) {
        const {offsetWidth, scrollWidth} = target;
        if (scrollWidth > offsetWidth) {
          target.addEventListener("mouseover", createTooltipListener);
          target.addEventListener("mouseout", removeTooltipListener);
        } else {
          target.removeEventListener("mouseover", createTooltipListener);
          target.removeEventListener("mouseout", removeTooltipListener);
        }
      }
    }
  });

  // Filter actionListData based on allowlistedFields
  const filteredActionListData = actionListData
    .filter(el => el.Entity.trim() !== "" && Object.keys(el.EntityResult).length > 0)
    .map(el => {
      const filteredEntityResult = filterKeys(el.EntityResult, allowlistedFields);
      return {...el, EntityResult: filteredEntityResult};
    })
    .filter(el => Object.keys(el.EntityResult).length > 0);

  const RiskRatingColors = {
    Low: "rgba(120, 217, 236, 1)",
    Medium: "rgba(255, 243, 113, 1)",
    High: "rgba(250, 144, 62, 1)",
    Critical: "rgba(236, 69, 59, 1)",
  };

  function updateHeaderInfo(entityData) {
    const entityResult = entityData.EntityResult;
    console.log(entityResult.type);
    if (entityResult.type === "threat-actor") {
      const sourceRegion = entityResult.locations.source[0].country;
      if (headerInfoTitleLogo && sourceRegion.iso2 && sourceRegion.iso2 !== "unknown") {
        headerInfoTitleLogo.innerHTML = `<span class="flag-icon flag-icon-${sourceRegion.iso2.toLowerCase()}"></span>`;
      } else {
        headerInfoTitleLogo.innerHTML = `<svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
<path d="M6.75 21C5.70833 21 4.82292 20.635 4.09375 19.9051C3.36458 19.1751 3 18.2888 3 17.246C3 16.2032 3.36458 15.3168 4.09375 14.5869C4.82292 13.857 5.70833 13.492 6.75 13.492C7.54167 13.492 8.25 13.7162 8.875 14.1646C9.5 14.613 9.95833 15.2021 10.25 15.9321C10.5208 15.807 10.8073 15.7183 11.1094 15.6662C11.4115 15.614 11.7083 15.588 12 15.588C12.2917 15.588 12.5885 15.614 12.8906 15.6662C13.1927 15.7183 13.4792 15.807 13.75 15.9321C14.0417 15.2021 14.5 14.613 15.125 14.1646C15.75 13.7162 16.4583 13.492 17.25 13.492C18.2917 13.492 19.1771 13.857 19.9062 14.5869C20.6354 15.3168 21 16.2032 21 17.246C21 18.2888 20.6354 19.1751 19.9062 19.9051C19.1771 20.635 18.2917 21 17.25 21C16.4583 21 15.7396 20.7758 15.0938 20.3274C14.4479 19.879 14 19.2898 13.75 18.5599C13.5208 18.3305 13.2552 18.148 12.9531 18.0124C12.651 17.8769 12.3333 17.8091 12 17.8091C11.6667 17.8091 11.349 17.8769 11.0469 18.0124C10.7448 18.148 10.4792 18.3305 10.25 18.5599C10 19.2898 9.55208 19.879 8.90625 20.3274C8.26042 20.7758 7.54167 21 6.75 21ZM6.75 18.7476C7.16667 18.7476 7.52083 18.6016 7.8125 18.3096C8.10417 18.0176 8.25 17.6631 8.25 17.246C8.25 16.8289 8.10417 16.4743 7.8125 16.1824C7.52083 15.8904 7.16667 15.7444 6.75 15.7444C6.33333 15.7444 5.97917 15.8904 5.6875 16.1824C5.39583 16.4743 5.25 16.8289 5.25 17.246C5.25 17.6631 5.39583 18.0176 5.6875 18.3096C5.97917 18.6016 6.33333 18.7476 6.75 18.7476ZM17.25 18.7476C17.6667 18.7476 18.0208 18.6016 18.3125 18.3096C18.6042 18.0176 18.75 17.6631 18.75 17.246C18.75 16.8289 18.6042 16.4743 18.3125 16.1824C18.0208 15.8904 17.6667 15.7444 17.25 15.7444C16.8333 15.7444 16.4792 15.8904 16.1875 16.1824C15.8958 16.4743 15.75 16.8289 15.75 17.246C15.75 17.6631 15.8958 18.0176 16.1875 18.3096C16.4792 18.6016 16.8333 18.7476 17.25 18.7476ZM1.5 11.9904V9.73797H5.46875L7.8125 2.5115C7.97917 2.11524 8.21875 1.83369 8.53125 1.66684C8.84375 1.5 9.20833 1.45829 9.625 1.54171L12 2.16738L14.375 1.54171C14.7917 1.45829 15.1562 1.5 15.4688 1.66684C15.7812 1.83369 16.0208 2.11524 16.1875 2.5115L18.5312 9.73797H22.5V11.9904H1.5ZM7.84375 9.73797H16.1563L14.25 3.91925L12 4.51364L9.75 3.91925L7.84375 9.73797ZM12.0312 9.61283H16.2188H7.8125H12.0312Z" fill="white"/>
</svg>`;
      }

      const firstObserved = entityResult.observed[0];
      const firstActivity = firstObserved.earliest || "N/A";
      const lastActivity = firstObserved.recent || "N/A";
      const sourceRegionName = sourceRegion.name || "N/A";
      const reportLink = entityResult.report_link || "N/A";

      headerInfoActivity.innerHTML = `
      <div class="header-info-activity-item"><div class="header-info-activity-item-key">First Activity</div> <div class="header-info-activity-item-value">${formatDate(firstActivity)}</div></div>
