  <link href="https://fonts.googleapis.com/css?family=Source Sans Pro"
        rel="stylesheet"/>
  <title>Get Detection Details</title>
</head>
<style>
  /* Root Variables */
  :root {
    --widget-bg: rgb(33, 44, 68);
    --widget-font-family: 'Source Sans Pro', sans-serif;
    --widget-text-color: rgb(255, 255, 255);
    --widget-scrollbar-size: 12px;
    --widget-row-highlight-bg: rgb(47 61 91);


    --divider-border-colcor: rgb(98, 117, 163);
    --divider-border-width: 2px;

    /*Logo styles*/
    --logo-size: 48px;
    --logo-color: rgb(255, 255, 255);
    /*Logo styles*/

    --header-title-font: 600 18px/22px var(--widget-font-family);
    --list-item-key-font: 600 12px/18px var(--widget-font-family);
    --list-item-value-font: 400 12px/18px var(--widget-font-family);
    --additional-title-font: 600 16px/18px var(--widget-font-family);
  }

  /*Normalize CSS */
  * {
    margin: 0;
    padding: 0;
    outline: none;
    box-sizing: border-box;
  }

  *:hover, *:focus, *:active {
    outline: none;
  }

  table {
    width: 100%;
    border-collapse: collapse;
    border-spacing: 0;
  }

  ol, ul {
    list-style: none;
  }

  button,
  input,
  textarea {
    font-family: inherit;
    background: transparent;
    border: 0;
    color: inherit;
    text-align: left;
    width: 100%;
  }

  input[type="checkbox"] {
    max-width: 14px;
    max-height: 14px;
    min-width: 14px;
    min-height: 14px;
  }

  button, a[href] {
    cursor: pointer;
  }

  /*Normalize CSS */


  /* Custom Scrollbar */
  ::-webkit-scrollbar {
    width: 18px;
    height: 18px;
  }

  ::-webkit-scrollbar-thumb {
    background: rgba(137, 151, 180, 1);
    border: 6px solid var(--widget-bg);
    border-radius: 63px;
  }

  ::-webkit-scrollbar-track {
    background: transparent;
    border: 4px solid transparent;
    border-radius: 44px;
  }

  ::-webkit-scrollbar-corner {
    background-color: transparent;
    border-color: transparent;
  }

  body {
    font-family: var(--widget-font-family), serif;
    color: var(--widget-text-color);
    background-color: var(--widget-bg);
    overflow: hidden;
  }

  /*Global styles*/
  .wrapper {
    overflow: auto;
    height: 100vh;
  }

  .divider-border-style {
    border: 0;
    border-color: var(--divider-border-colcor);
    border-style: solid;
  }

  .input {
    width: auto;
    max-width: 150px;
    border: 1px solid #6275A3;
    border-radius: 4px;
    padding: 4px 8px;
    background-color: transparent;
    font-size: 14px;
    color: var(--widget-text-color);
  }

  .input::placeholder {
    color: var(--widget-text-color);
  }

  .logo {
    min-width: var(--logo-size);
    min-height: var(--logo-size);
    max-width: var(--logo-size);
    max-height: var(--logo-size);
    color: var(--logo-color);
  }

  .tag {
    display: flex;
    flex-wrap: wrap;
    gap: 8px;
  }

  .tag-item {
    padding: 2px 12px;
    background-color: rgb(106, 113, 128);
    border-radius: 2px;
  }

  /*Global styles*/

  .header {
    display: flex;
    align-items: flex-start;
    justify-content: space-between;
    gap: 12px;
    margin-bottom: 12px;
  }

  .header-wrapper {
    display: flex;
    align-items: center;
    flex-wrap: wrap;
    gap: 8px;
    margin-bottom: 8px;
  }

  .header-title {
    font: var(--header-title-font);
    word-break: break-word;
    text-transform: uppercase;
  }

  .header-title-link {
    display: flex;
    align-items: center;
    color: rgb(138 180 248);
  }

  .header-subtitle {
    font: 400 14px/18px var(--widget-font-family);
  }

  .header-events {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 6px;
  }

  .header-events-label {
    display: flex;
    align-items: center;
    gap: 4px;
    cursor: pointer;
  }

  .header-events-label-text {
    font: 600 12px/16px var(--widget-font-family);
  }

  .content-events {
    min-height: 76px;
    display: flex;
    align-items: center;
    gap: 12px;
    background-color: var(--widget-bg);
    position: sticky;
    top: 0;
    z-index: 99;
  }

  .content-details-item {
    display: flex;
    align-items: center;
    gap: 8px;
  }

  .content-details-item-key {
    font: 400 12px/16px var(--widget-font-family);
    color: var(--widget-text-color);
  }

  .content-details-item-value {
    font: 600 12px/16px var(--widget-font-family);
    color: var(--widget-text-color);
  }

  .content-details-item-risk-score {
    display: flex;
    gap: 4px;
    padding-right: 4px;
    border-radius: 2px;
    border-width: 1px;
    border-style: solid;
  }

  .content-details-item-risk-score-value {
    display: flex;
    align-items: center;
    padding: 0 6px;
    font: 600 12px/12px var(--widget-font-family);
    color: rgb(22, 22, 37);
  }

  .content-details-button {
    font: 600 12px/16px var(--widget-font-family);
    width: auto;
    background-color: rgb(98 117 163);
    padding: 2px 8px;
    border-radius: 4px;
  }

  .content-list-wrapper {
    flex: 1;
    position: relative;
    overflow: hidden;
  }

  .content-list-wrapper::before {
    content: "";
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    width: 100px;
    background: linear-gradient(to left, rgb(34 43 62), rgba(33, 44, 68, 0));
