          height: 100%;
          overflow-y: auto;
      }

      .container-content {
          float: left;
          width: 68%;
          padding-left: 3%;
          border-left: 1px solid #576B95;
      }

      .title-logo {
          display: flex;
          width: 100%;
          justify-content: space-between;
      }

      .vulnerabilities-count {
          display: flex;
          align-items: center;
          justify-content: center;
          margin-bottom: 24px;
          border-top: 1px solid #576B95;
          padding-top: 18px;
          flex-wrap: wrap;
      }

      .vulnerabilities-count h2 {
          font-weight: bold;
          font-size: 72px;
          text-shadow: 1px 1px #fff;
          margin: 0;
      }

      .vulnerabilities-count h3 {
          font-weight: bold;
          font-size: 22px;
          margin: 0;
          padding-left: 3%;
          width: 200px;
      }

      .vulnerability-severities {
          display: flex;
