      }

      .vulnerability-severities .severity-color {
          margin-right: 10px;
          width: 32px;
          height: 32px;
          border-radius: 50%;
          display: flex;
          justify-content: center;
          align-items: center;
      }

      .severity-critical-color {
          background-color: #FF5A50;
      }

      .severity-high-color {
          background-color: #FA903E;
      }

      .severity-medium-color {
          background-color: #FFED2E;
      }

      .severity-low-color {
          background-color: #42C3D0;
      }

      .severity-unspecified-color {
          background-color: #669DF6;
      }

